const Hello = (props) => {
  return (
    <div>
      <p>Hello {props.name} you are {props.age} years old.</p>
    </div>
  );
};

const Exercise = (props) => {
  return (
    <div>
      <p>{props.part} {props.exercise}</p>
    </div>
  )
}

const App = () => {
  const now = new Date().toISOString();
  const course = "Half Stack app development";
  const part1 = 'Fundamentals of React';
  const part2 = 'Using props to pass data';
  const part3 = 'State of a component';
  const exercises1 = 10;
  const exercises2 = 7;
  const exercises3 = 14;
  return (
    <div>
      <h1>Greetings</h1>
      <Hello name="Andrei" age={17 + 10}/>
      <p>{now}</p>
    <div>
      <h2>{course}</h2>
      <Exercise part={part1} exercise={exercises1} />
      <Exercise part={part2} exercise={exercises2} />
      <Exercise part={part3} exercise={exercises3} />
      <p>Total Number of exercises {exercises1 + exercises2 + exercises3}</p>
    </div>

    </div>
  );
};

export default App;
